import { useRef, useEffect, useState } from "react";
import classes from './Counter.module.css';
import { useSelector, useDispatch } from 'react-redux'

const Counter = () => {
  const amountRef = useRef(0);
  const [showVal, setshowVal] = useState(0);
  const [showcounter, setshowcounter] = useState(true);

  // receive the state from react redux, and return the part of the state we want to extract
  // by doing this redux will automatically set up a subscription to the redux store for this component
  // your component will be updated and will receive the latest counter automatically whenever that data changes in the redux store
  // if this for some reason would be unmounted from the DOM, redux will automatically clear the subscription for you
  const counter = useSelector(state => state.counter);

  const dispatch = useDispatch();

  const toggleCounterHandler = () => {
    setshowcounter((prev) => !prev);
  };

  const increaseHandler = () => {
    // with extra payload
    dispatch({ type: 'increase', amount: amountRef });
  }
  const incrementHandler = () => {
    dispatch({type : 'increment'});
  };

  const decrementHandler = () => {
    dispatch({type : 'decrement'});

  };
  const changeValueHandler = (event) => {

    setshowVal(() => amountRef.current.value);
    return event.value;
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}> {counter} </div>
      {
        showcounter &&
      <div>
        <button onClick={incrementHandler}>Increment</button>
          <button onClick={increaseHandler}> amount increased by {amountRef.current.value > 0 ? amountRef.current.value : 0}</button>
          <input onClick={changeValueHandler} value={amountRef.current.value >= 0 ? amountRef.current.value : 0} type="number" name="" ref={amountRef} id="" />
        <button onClick={decrementHandler}>Decrement</button>
      </div>
      }
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
