import classes from './Counter.module.css';
import { useSelector } from 'react-redux'

const Counter = () => {
  // receive the state from react redux, and return the part of the state we want to extract
  // by doing this redux will automatically set up a subscription to the redux store for this component
  // your component will be updated and will receive the latest counter automatically whenever that data changes in the redux store
  // if this for some reason would be unmounted from the DOM, redux will automatically clear the subscription for you
  const counter = useSelector(state => state.counter)

  const toggleCounterHandler = () => {
    counter.dispatch('increment');
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}> {counter} </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
