import { createStore } from 'redux';
const counterReducer = (state = { counter: 0 }, action = "") => {
    // the type property is set in the useDispatch function set in the components using the redux store
    if (action.type === 'increment') {
        return {
            // because this is returned when using the useSelector, we can access the "counter" from the useSelector hook when we pass in a function to its parameter
            counter: state.counter + 1,
        }
    }
    if (action.type === 'decrement') {
        return {
            counter : state.counter -1,
        }
    }
    return state;
}

const store = createStore(counterReducer);


export default store;
