import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux'
import { useEffect } from 'react';
import classes from './Counter.module.css';
// useStore = get access to the whole sotr
// useSelector get access to a part of a state managed by the store
// connect is used by classbased components, used as a wrapper of our component to connect the class component to the store
let incrementInterval = -1;
let decrementInterval = -1;
let speedUpTimer = -1;
let milliseconds = 60;

const Counter = () => {
  /*
      auto sets up a subscription to the reux store for this component,
      so the component will be updated and get the latest counter value
      whenever that data changes in the redux store

      changes to the reux store causea automatically the cpo,ponent to be re-excecuted

      react redux also automatically clears the subscription if the component gets unmounted
   */
  const counter = useSelector(state => state.counter);
  const dispatch = useDispatch();
  console.log("counter: ", counter);

  useEffect(() => {
    if (counter <= 0) {
      dispatch({ type: 'reset' });
    }

    return () => {
      stopCounter();
    }
  }, [counter, dispatch]);


  const runIncrementInterval = () => {
    incrementInterval = setInterval(() => {
      dispatch({ type: 'increment' });
    }, milliseconds);
  }
  const runDecrementInterval = () => {
    decrementInterval = setInterval(() => {
      dispatch({ type: 'decrement' });
    }, milliseconds);
  }
  const toggleCounterhandler = () => {
    // distpatch actions
  };

  const incrementCounter = () => {
    // here we give a empty object a type property which is used in the redux store when accessing the "action"
    dispatch({ type: 'increment' });
  };

  const decrementCounter = () => {
    if (counter === 0) return;
    dispatch({ type: 'decrement' });
  };
  const delayedIncrement = () => {
    let runOnce = true;
    if (runOnce) {
      runIncrementInterval();
      speedUpTimer = setTimeout(() => {
        milliseconds = 30;
        clearInterval(incrementInterval);
        runIncrementInterval();
      }, 2000);
    }
  };
  const delayedDecrement = () => {
    let runOnce = true;
    if (runOnce) {
      clearInterval(decrementInterval);
      runDecrementInterval();
      milliseconds = 30;
      speedUpTimer = setTimeout(() => {
        runDecrementInterval();
      }, 2000);
    }
  };
  const stopCounter = () => {
    clearInterval(incrementInterval);
    clearInterval(decrementInterval);
    clearInterval(speedUpTimer);
    milliseconds = 60;
  }
  return (
    <main className={classes.counter}>
      <h1>Redux counter</h1>
      <div className={classes.value}>{counter}</div>
      <div>
        <span><button onMouseLeave={stopCounter} onMouseUp={stopCounter} onMouseDown={delayedIncrement} onClick={incrementCounter}>Increment</button></span>
        <span><button onMouseLeave={stopCounter} onMouseUp={stopCounter} onMouseDown={delayedDecrement} onClick={decrementCounter}>Decrement</button></span>
      </div>
      <button onClick={toggleCounterhandler}>Toggle counter</button>
    </main>
  );
};

export default Counter;
